    private void agregarCandidatoATabla(Candidatos candidato) {
        String Orientacion = "";
        if(candidato.getOrientacion_politica()){
            Orientacion = "Derecha";
        }else Orientacion = "izquierda";

            Object[] fila = {
                candidato.getCedula(),
                candidato.getNombre(),
                candidato.getCiudad_origen(),
                Orientacion,
                candidato.getPartido_politico(),
                candidato.getLista_promesas()
            };
        
            // Agregar la nueva fila al modelo de la tabla
            modelo.addRow(fila);
    }
